const Post = (props) => {
    return (
        //alternative withRouter vs query string from getServerSideProps
        <h1>Post Page #{props.router.query.id}</h1>
    )
}

export async function getServerSideProps({ query }) {
    console.log(query)
    return {
        props: { query }, // will be passed to the page component as props
    }
}

export default withRouter(Post);
