// import { withRouter } from "next/router"
import Axios from "axios"

const Post = ({ id, comments }) => {
    return (
        //alternative withRouter vs query string from getServerSideProps
        <div>
            <h1>Comments for Post #{id}</h1>
            {comments.map((comment) => {
                return <p>{comments.email}</p>
            })}
        </div>
    )
}

export async function getServerSideProps({ query }) {
    const res = await Axios.get(`https://jsonplaceholder.typicode.com/comments?postId=3`)
    const { data } = res
    console.log(data[0])

    return {
        props: { ...query, comments: data }, // will be passed to the page component as props
    }
}

export default Post;
