// import { withRouter } from "next/router"
import Axios from "axios"

const Post = (props) => {
    return (
        //alternative withRouter vs query string from getServerSideProps
        <h1>Post Page #{props.router.query.id}</h1>
    )
}

export async function getServerSideProps({ query }) {
    const res = await Axios.get(`https://jsonplaceholder.typicode.com/comments?postId=1`)
    const { data } = res
    console.log(data)
    return {
        props: { query }, // will be passed to the page component as props
    }
}

export default Post;
